-- Idea is to find all the X, and check 8 directions, to see if can find XMAS
-- sc1 - to check 6 directions, up, down, leftdown, rightdown, rightup, leftup
-- sc2 - just to check XMAS and SAMX for each line


alter table #input alter column r int 

drop table if exists #brk

; with cte as 
(select r, pos = 1, curr = left(col, 1), rem = right(col, len(col)-1)
from #input 
union all 
select r, pos+1, left(rem, 1), right(rem, len(rem)-1)
from cte 
where len(rem) > 0
)

select * into #brk 
from cte 
option (maxrecursion 0)


drop table if exists #Q1_sc1

select 
current_pos_found_count = case when curr = 'X' 
                               then  
                                    case when  -- up
                                            (select curr from #brk b2 where b2.r = b.r - 1 and b2.pos = b.pos)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 2 and b2.pos = b.pos)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 3 and b2.pos = b.pos)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end 
                                    + 
                                    case when --down 
                                            (select curr from #brk b2 where b2.r = b.r + 1 and b2.pos = b.pos)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 2 and b2.pos = b.pos)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 3 and b2.pos = b.pos)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end
                                    + 
                                    case when -- left down
                                            (select curr from #brk b2 where b2.r = b.r + 1 and b2.pos = b.pos - 1)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 2 and b2.pos = b.pos - 2)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 3 and b2.pos = b.pos - 3)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end
                                    + 
                                    case when -- right down
                                            (select curr from #brk b2 where b2.r = b.r + 1 and b2.pos = b.pos + 1)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 2 and b2.pos = b.pos + 2)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r + 3 and b2.pos = b.pos + 3)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end
                                    + 
                                    case when -- right up
                                            (select curr from #brk b2 where b2.r = b.r - 1 and b2.pos = b.pos + 1)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 2 and b2.pos = b.pos + 2)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 3 and b2.pos = b.pos + 3)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end
                                    + 
                                    case when -- left up
                                            (select curr from #brk b2 where b2.r = b.r - 1 and b2.pos = b.pos - 1)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 2 and b2.pos = b.pos - 2)
                                            + 
                                            (select curr from #brk b2 where b2.r = b.r - 3 and b2.pos = b.pos - 3)
                                            = 'MAS'
                                        then 1 
                                        else 0
                                    end
                                else 0 
                            end 
, * 
into #Q1_sc1
from #brk b


