drop table if exists #Q1_sc2 

select 
r
, XMAS_cnt =  (len(col) - len(replace(col, 'XMAS', ''))) / 4
, SAMX_cnt = (len(col) - len(replace(col, 'SAMX', ''))) / 4
into #Q1_sc2
from #input 


; with rpt as 
(
    select total = sum(current_pos_found_count) from #Q1_sc1
    union all 
    select sum( XMAS_cnt + SAMX_cnt) from #Q1_sc2
)
select res = sum(total)
from rpt 






