                                 (select curr from #brk b1 where b1.pos = b.pos+1 and b1.r = b.r + 1)
                                 in ('MS', 'SM')
                                 and
                                 (select curr from #brk b1 where b1.pos = b.pos+1 and b1.r = b.r - 1)
