-- give each island an id, and find all the (r, c) in the same island 
-- for each character, it will have perimeter = 4. Once find there's a same character on the left, right, up, down, then -1 for each



drop table if exists #base 

; with cte as 
(
select 
r = cast(id as int)
, c = 1 
, ch = left(col, 1)
, rem = right(col, len(col) - 1)
from #input

union all 

select 
r
, c + 1 
, ch = left(rem, 1)
, rem = right(rem, len(rem) - 1)
from cte
where len(rem) > 0
)
select 
pos = cast(mc*(r-1) + c as varchar(100))
, r, c, ch  into #base 
from cte, (select mc = max(c) from cte) _
OPTION(maxrecursion 0)


drop table if exists #island_row

; with cte as 
(
select 
* 
, same_as_left = iif(ch = lag(ch, 1) over (partition by r order by c), 1, 0)
, row_start = iif(ch = lag(ch, 1) over (partition by c order by r), 0, 1)
-- , c_end = iif(ch = lead(ch, 1) over (partition by c order by r), 0, 1)
from #base 
)
, cte2 as 
(
 select 
 ch
 , r
 , c
 , start_col = max(iif(same_as_left = 1, NULL, c)) over(partition by r order by c)
 , row_start 
 from cte 
) 
select 
r
, ch 
, start_col 
, end_col = max(c) -- for each r, ch, ch_r_start, where does it end ?
, row_start = min(row_start)
, island_segment = ROW_NUMBER() over(order by r,  start_col) 
into #island_row
from cte2
group by r, ch, start_col


-- select * from #island_row


drop table if exists #island_segments
; 
with cte as 
(
select 
iter = 1 
, r
, ch
, start_col
, end_col
, curr_segment = island_segment
, root_segment = island_segment
from #island_row 
where row_start = 1

union all 

select 
iter + 1 
, r.r
, r.ch
, r.start_col
, r.end_col
, curr_segment = r.island_segment
, root_segment
from cte c inner join #island_row r
on r.r = c.r + 1 and c.ch = r.ch
and 
(r.start_col between c.start_col and c.end_col 
    or 
c.start_col between r.start_col and r.end_col
)

) 
select distinct root_segment, curr_segment 
into #island_segments
 from cte 
order by 1, 2
OPTION (maxrecursion 0)


drop table if exists #MasterGroups

select r1.root_segment, min(r2.root_segment) New_root_segment
into #MasterGroups
from #island_segments r1
	cross apply (select top 1 *
					from #island_segments r2
					where r2.root_segment <= r1.root_segment
						and r2.curr_segment = r1.curr_segment 
                    order by r2.root_segment
				) r2
group by r1.root_segment


-- select * from #MasterGroups

drop table if exists #island_segment_consolidated

select distinct root_segment = B.New_root_segment, A.curr_segment 
into #island_segment_consolidated
from 
#island_segments A inner join #MasterGroups  B
on A.root_segment = B.root_segment



