drop table if exists #island_perimeter

; with cte as 
(
select seg.*, b.ch,  b.r, b.c from #island_segment_consolidated seg inner join #island_row i_row 
on seg.curr_segment = i_row.island_segment 
inner join #base b on i_row.r = b.r and b.c between i_row.start_col and i_row.end_col
) 
select 
cte.*
, size = 1 
, perimeter = 4 - iif(ch = lag(ch, 1) over (partition by r order by c), 1 , 0 ) -- left
                - iif(ch = lead(ch, 1) over (partition by r order by c), 1 , 0 ) --right
                - iif(ch = lag(ch, 1) over (partition by c order by r), 1 , 0 ) -- up
                - iif(ch = lead(ch, 1) over (partition by c order by r), 1 , 0 ) --down
into #island_perimeter
from cte 


select res = sum(size* p )
from (
    select
    root_segment
    , size = sum(size)
    , p =  sum(perimeter)
    from #island_perimeter
    group by root_segment
) _



