select res = sum(sides * sizes) 
from (
    select 
    root_segment
    , sides = sum(adjust_top_start + adjust_bottom_start + adjust_left_start + adjust_right_start ) 
    , sizes = count(*) from #line_start
    group by root_segment
) _
 



