-- Q1 ideas

-- for each character , find the island, concat the (r, c) as the island identifier
-- search top, left, down and right, to avoid infinite loop, as SQL cannot remember the previous step
-- find the how many characters in the same island -- size of the island
-- calculate the total perimeter of the island 
-- get the result as sum(size * perimeter)
-- +4 for each one as parameter
-- if find the same character on the top, left, right, and down, then -1 for each direction 

drop table if exists #base 

; with cte as 
(
select 
r = cast(id as int)
, c = 1 
, ch = left(col, 1)
, rem = right(col, len(col) - 1)
from #input

union all 

select 
r
, c + 1 
, ch = left(rem, 1)
, rem = right(rem, len(rem) - 1)
from cte
where len(rem) > 0
)
select pos = cast(mc*(r-1) + c as varchar(100)),  * into #base 
from cte, (select mc = max(c) from cte) _
OPTION(maxrecursion 0)


create index idx_r on #base(r)
create index idx_c on #base(c)

drop table if exists #islands 


; with cte as 
(
    select 
    root_pos = pos
    , iter = 1 
    , r
    , c
    , ch
    , pos 
    , pos_str = concat( '(', cast(pos as varchar(max)), ')')

    from #base
    union all 
    select
    root_pos
    , iter + 1
    , b.r
    , b.c
    , cte.ch
    , b.pos
    , concat( pos_str + ',' , '(' , b.pos, ')' )
    from cte inner join #base b
    on cte.ch = b.ch
    and (
           (abs (b.r - cte.r) = 1 and cte.c = b.c)
           or
           (abs (b.c - cte.c) = 1 and cte.r = b.r) 
        )

    and charindex('(' + b.pos + ')' , pos_str) = 0
    -- and pos_str not like '%(' + cast(b.pos as varchar) + ')%' -- current node is not seen
)

select distinct root_pos, ch, r, c, pos 
into #islands from cte 
order by 1 
OPTION(maxrecursion 0)


drop table if exists #islands_split

; with cte as 
(
select
island_area = STRING_AGG(pos, '|') within group (order by pos)
, island_pick = ROW_NUMBER() over( partition by STRING_AGG(pos, '|') within group (order by pos)
                                order by root_pos
                                )
, root_pos
 from #islands
group by root_pos
) 

select 
island_id = dense_rank() over(order by i.root_pos)
, i.*
into #islands_split
from cte c inner join #islands i 
on c.root_pos = i.root_pos
where island_pick = 1  -- all position in the same island will generate the same island, just use the first one


