drop table if exists #perimeter

select 
A.*
, p = 4 - case when up.r is not null then 1 else 0 end
                - case when down.r is not null then 1 else 0 end
                - case when lt.r is not null then 1 else 0 end
                - case when rt.r is not null then 1 else 0 end
into #perimeter
from #islands_split A
left outer join #base up on A.ch = up.ch and A.c = up.c and A.r - up.r = 1 
left outer join #base down on A.ch = down.ch and A.c = down.c and down.r - a.r = 1 
left outer join #base lt on A.ch = lt.ch and a.r = lt.r and a.c - lt.c = 1
left outer join #base rt on a.ch = rt.ch and a.r = rt.r and rt.c - a.c = 1


