-- Q1 ideas

-- for each character , find the island, concat the (r, c) as the island identifier
-- search top, left, down and right, to avoid infinite loop, as SQL cannot remember the previous step
-- find the how many characters in the same island -- size of the island
-- calculate the total perimeter of the island 
-- get the result as sum(size * perimeter)
-- +4 for each one as parameter
-- if find the same character on the top, left, right, and down, then -1 for each direction 

drop table if exists #base 

drop table if exists #base_0 

drop table if exists #islands

; with cte as 
(
select 
r = cast(id as int)
, c = 1 
, ch = left(col, 1)
, rem = right(col, len(col) - 1)
from #input

union all 

select 
r
, c + 1 
, ch = left(rem, 1)
, rem = right(rem, len(rem) - 1)
from cte
where len(rem) > 0
)
select pos = cast(mc*(r-1) + c as varchar(100)),  * into #base_0  
from cte, (select mc = max(c) from cte) _
OPTION(maxrecursion 0)


select Y= r,X = c, MaxX = max(r) over() ,  MaxY = max(c) over (), * into #base from #base_0 


alter table #base drop column rem 

;with i as
(select *
, IsContX = iif(lag(ch) over(partition by Y order by X) = ch, 1, 0)  -- same as left one?
, IsFirstY = iif(lag(ch) over(partition by X order by Y) = ch, 0, 1)  --same as up one ?
, IsLastY = iif(lead(ch) over(partition by X order by Y) = ch, 0, 1)  -- same as down one ?
from #base
)
, i1 as
(select Y, X, ch, IsContX = iif(IsContX = 1, null, X ) ,FirstX = max( iif(IsContX = 1, null, X ) ) 
                                over(partition by Y order by x) 
        , IsFirstY, IsLastY
from i
)
 select  
ch
, Y
, FirstX -- for each character on each line
, LastX = max(X)  -- last x for each character on each line
, IsFirstY = min(IsFirstY)
, IsLastY = min(IsLastY)
, island_segment = row_number() over(order by Y, FirstX) -- island segments 
, Xs = string_agg(cast(X as varchar(max)), ',') 
, ID =  row_number() over(order by Y, FirstX) 
into #Islands
from i1
group by ch, Y, FirstX


select * from #Islands

create unique clustered index IX_#Islands on #Islands(Y, ch, FirstX)

;with rec as
	(select ch, Y, FirstX, LastX, island_segment gID, island_segment IslandID, 0 StepID
		from #Islands
		where IsFirstY = 1 
		union all
		select r.ch, i.Y, i.FirstX, i.LastX, r.gID, i.island_segment IslandID, StepID + 1
		from rec r
			inner join #Islands i on i.ch = r.ch
								and i.Y = r.Y + 1
								and (i.FirstX between r.FirstX and r.LastX
