-- Idea: go through all the possible steps, stop if both exceed the prize X and Y 
--- check all the combination can reach the target, get the min(cost) for each group
-- sum the min cost to get result

drop table if exists #landing

select 
grp =  (ROW_NUMBER() over (order by cast(id as int)) - 1) / 3 
, rn = ROW_NUMBER() over (order by cast(id as int)) 
, col = replace(replace(replace(col,'+', '.'), ', ', '.'), '=', '.')
into #landing
from #input

drop table if exists #base

; with cte as 
(
select 
grp
, rn
, A_X = cast(case when rn % 3 = 1 then PARSENAME(content, 3) else NULL end as bigint)
, A_Y = cast(case when rn % 3 = 1 then PARSENAME(content, 1) else NULL end as bigint)
, B_X = cast(case when rn % 3 = 2 then PARSENAME(content, 3) else NULL end as bigint)
, B_Y = cast(case when rn % 3 = 2 then PARSENAME(content, 1) else NULL end as bigint)
, prize_X = cast(case when rn % 3 = 0 then PARSENAME(content, 3) else NULL end as bigint)
, prize_Y = cast(case when rn % 3 = 0 then PARSENAME(content, 1) else NULL end as bigint)
 from #landing cross apply (select content = right(col,  len(col) - charindex(':', col))) _content
) 
select 
grp = grp + 1 
, A_X = max(A_X)
, A_Y = max(A_Y)
, B_X = max(B_X)
, B_Y = max(B_Y)
, prize_X = max(prize_X)
, prize_Y = max(prize_Y)
into #base
from cte
group by grp



drop table if exists #iteration

; with cte as 
(select n = 1
union all 
select n + 1 from cte
where n < 100
)
select n into #iteration from cte


drop table if exists #mvmt

select 
grp
, A_iter = an
, B_iter = bn
, curr_x = an*A_X + bn*B_X
, curr_Y = an*A_Y + bn*B_Y
, A_X
, A_Y
, B_X
, B_Y
, prize_X
, prize_Y
into #mvmt
from #base 
, (select an = a.n, bn = b.n from #iteration a, #iteration b ) iter
where an*A_X + bn*B_X <= prize_X 
and an*A_Y + bn*B_Y <= prize_Y


drop table if exists #costs

select 
grp
, cost = (3*A_iter + 1 * B_iter) 
into #costs
from #mvmt 
where curr_x = prize_X 
and curr_y = prize_Y
 
-- one group can have multiple ways to reach the target, get the lowest cost
drop table if exists #lowest_costs

select * 
into #lowest_costs
from #costs c
where not exists (select * from #costs c2 where c.grp = c2.grp and c2.cost < c.cost)

-- 36838
select res = sum(cost)
from #lowest_costs




