---A*Ax + B*Bx = c
-- A*Ay + B*By = d 
-- Ax, Bx, Ay, By, c, d are known. 

--select * from #base

drop table if exists #base_q2

select 
*
, prize_X_2 = prize_X + 10000000000000
, prize_Y_2 = prize_Y + 10000000000000
into #base_q2 
from #base

drop table if exists #matrix_calc

select 
b.*
, A = case when cast(A as bigint) = A then cast(A as bigint) else NULL end
, B = case when cast(B as bigint) = B then cast(B as bigint) else NULL end
into #matrix_calc
from #base_q2 b cross apply 
    (select A = 1.0*(prize_X_2 * B_Y - prize_Y_2 * B_X)  / nullif( A_X * B_Y - A_Y * B_X  , 0)
           ,B = 1.0*(prize_X_2 * A_Y - prize_Y_2 * A_X)  / nullif( B_X * A_Y - B_Y * A_X  , 0)
    ) _


-- 83029436920891
select res = sum(A*3 + B) from #matrix_calc

