-- Idea:  Start from S, facing east, for each step, check the next movable location,
-- and add current location to the seen string, to avoid loops
-- based on the direction, turn or move along

drop table if exists #base

select 
r = cast(#input.id as int)
, c = n.[value]
, ch = SUBSTRING(col, n.[value], 1)
, loc = cast (concat('(',  cast(#input.id as int) , ',', n.[value] , ')') as varchar(max))
into #base
from #input , generate_series_RL(1, (select len(col) from #input where id  = 1), 1 ) n
option (maxrecursion 0)


drop table if exists #paths


; with c_iter as 
(select cnt = count(*) from #base where ch = '.')
, cte as
(select 
iter = 1
, face = 'E'
, direction = cast('E' as varchar(max))
, r
, c
, seen = loc
, total = 0
, prev = ''
 from #base
where ch = 'S'

union all 

select 
iter + 1
, face = nxt_face
, direction + nxt_face
, r = nxt.r
, c = nxt.c
, seen = seen + nxt.loc
, total = total + 
                case when c.face = 'E' then
                            case when nxt.r = c.r and nxt.c = c.c + 1 then 1
                                    when nxt.r = c.r - 1 and c.c = nxt.c then 1000 + 1
                                    when nxt.r = c.r +1 and c.c = nxt.c then 1000 + 1
                                end
                            when c.face = 'S' then 
                                case when nxt.r = c.r + 1 and c.c = nxt.c then 1
                                    when nxt.r = c.r and nxt.c = c.c + 1 then 1000 + 1
                                    when nxt.r = c.r and nxt.c = c.c - 1 then 1000 + 1
                                end
                            when c.face = 'W' then 
                                case when nxt.r = c.r and nxt.c = c.c - 1 then 1
                                    when nxt.r = c.r + 1 and nxt.c = c.c then 1000 + 1
                                    when nxt.r = c.r - 1 and nxt.c = c.c then 1000+ 1
                                end
                            when c.face = 'N' then 
                                case when nxt.r = c.r-1 and c.c = nxt.c then 1
                                    when nxt.r = c.r  and c.c = nxt.c - 1  then 1000+ 1
                                    when nxt.r = c.r  and c.c = nxt.c + 1  then 1000+ 1
                                end
                        end
, nxt.ch
from cte c inner join #base nxt 
on
    case when c.face = 'E' then
        case when nxt.r = c.r and nxt.c = c.c + 1 then 1 
                when nxt.r = c.r - 1 and c.c = nxt.c then 1 
                when nxt.r = c.r + 1 and c.c = nxt.c then 1
            end
        when c.face = 'S' then 
            case when nxt.r = c.r + 1 and nxt.c = c.c  then 1 
                when nxt.r = c.r and nxt.c + 1 = c.c then 1 
                when nxt.r = c.r and nxt.c - 1 = c.c then 1
            end
        when c.face = 'W' then 
            case when nxt.r = c.r and nxt.c = c.c - 1 then 1 
                when nxt.r = c.r + 1 and nxt.c = c.c then 1 
                when nxt.r = c.r - 1 and nxt.c = c.c then 1
            end
        when c.face = 'N' then 
            case when nxt.r = c.r - 1 and nxt.c = c.c then 1 
                 when nxt.r = c.r  and nxt.c - 1 = c.c then 1 
                 when nxt.r = c.r  and nxt.c + 1 = c.c then 1
            end
    end = 1 

cross apply (select nxt_face = case when c.face = 'E' then
                                    case when nxt.r = c.r and nxt.c = c.c + 1 then 'E'
                                            when nxt.r = c.r - 1 and c.c = nxt.c then 'N' 
                                            when nxt.r = c.r +1 and c.c = nxt.c then 'S'
                                        end
                                    when c.face = 'S' then 
                                        case when nxt.r = c.r + 1 and c.c = nxt.c then 'S'
                                            when nxt.r = c.r and nxt.c = c.c + 1 then 'E' 
                                            when nxt.r = c.r and nxt.c = c.c - 1 then 'W'
                                        end
                                    when c.face = 'W' then 
                                        case when nxt.r = c.r and nxt.c = c.c - 1 then 'W'
                                            when nxt.r = c.r + 1 and nxt.c = c.c then 'S' 
                                            when nxt.r = c.r - 1 and nxt.c = c.c then 'N'
                                        end
                                    when c.face = 'N' then 
                                        case when nxt.r = c.r-1 and c.c = nxt.c then 'N' 
                                            when nxt.r = c.r  and c.c = nxt.c - 1  then 'E'
                                            when nxt.r = c.r  and c.c = nxt.c + 1  then 'W'
                                        end
          end) _ 
where nxt.ch in ('.', 'E') and CHARINDEX(nxt.loc, seen) = 0
and iter < (select cnt from c_iter) and prev != 'E'
)
select 
*
into
#paths from cte 
option(maxrecursion 0)



select top 1 res = total 
from #paths 
where prev = 'E'
order by total




