, res = cast(s3_2  as bigint) -- this is the result
from #base 
cross apply (select s1_1 = cast( (n * 64) ^ n as bigint ) ) s1_mix
cross apply (select s1_2 = s1_1 % 16777216 ) s1_prune
cross apply (select s2_1 = (s1_2 / 32) ^ s1_2 ) s2_mix
cross apply (select s2_2 = s2_1 % 16777216 ) s2_prune
cross apply (select s3_1 = (s2_2 * 2048) ^ s2_2 ) s3_mix
cross apply (select s3_2 = s3_1 % 16777216 ) s3_prune

union all 

select id + 1 
, seed = res 
, res = s3_2 
from cte 
cross apply (select s1_1 = (res * 64) ^ res ) s1_mix
cross apply (select s1_2 = s1_1 % 16777216 ) s1_prune
cross apply (select s2_1 = (s1_2 / 32) ^ s1_2 ) s2_mix
cross apply (select s2_2 = s2_1 % 16777216 ) s2_prune
cross apply (select s3_1 = (s2_2 * 2048) ^ s2_2 ) s3_mix
cross apply (select s3_2 = s3_1 % 16777216 ) s3_prune
where id < 2000
) 
select * into #calc from cte
where id = 2000 
order by 1  desc 
option (maxrecursion 0)

