drop table if exists #walk

-- alter table #brk drop COLUMN rem 

; with cte as 
(
select id = 1,  step = 0,  r, c,  dir = 'U'  from #brk where ch = '^'
union all 
select 
id + 1
, step + 
   case when curr.dir = 'U' then abs(nxt.up_r + 1 - curr.r)
        when curr.dir = 'D' then abs(nxt.down_r - 1 - curr.r)
        when curr.dir = 'L' then abs(nxt.left_c + 1 - curr.c)
        when curr.dir = 'R' then abs(nxt.right_c - 1 - curr.c) 
       end 
,  r =  case when curr.dir = 'U' then nxt.up_r + 1  
             when curr.dir = 'D' then nxt.down_r -1  
             when curr.dir = 'L' then curr.r  
             when curr.dir = 'R' then curr.r  
        end
,  c = case when curr.dir = 'U' then curr.c 
        when curr.dir = 'D' then curr.c 
        when curr.dir = 'L' then nxt.left_c + 1 
        when curr.dir = 'R' then nxt.right_c - 1 
        end

, dir = case when curr.dir = 'U' then 'R'
            when curr.dir = 'D' then 'L'
            when curr.dir = 'L' then 'U'
            when curr.dir = 'R' then 'D'
        end

from cte curr inner join #nxt nxt 
on curr.r= nxt.r and curr.c = nxt.c
where case  when curr.dir = 'U' then up_r
            when curr.dir = 'D' then down_r 
            when curr.dir = 'L' then left_c
            when curr.dir = 'R' then right_c
       end is not null 
) 
-- adding the final point on the map before exiting 
select *  into #walk
 from cte
option (maxrecursion 0 )


-- select * from #walk 

drop table if exists #curr_next

select
id 
, curr_r = r
, curr_c = c 
, next_r = case when lead(r, 1) over (order by id) is null 
                then case when dir = 'U' then 1
                          when dir = 'D' then (select max(r) from #brk )
                          when dir in ('R', 'L') then r
                     end
                else
                lead(r, 1) over (order by id)
           end 
, next_c = case when lead(c, 1) over (order by id) is null 
                then case when dir in ('U', 'D') then c
                          when dir = 'R' then (select max(c) from #brk)
                          when dir = 'L' then 1
                     end
                else
                lead(c, 1) over (order by id)
           end 
into #curr_next
from #walk 


-- select * from #curr_next


-- 4883
select res = count(*) 
from 
(
    select distinct map.r, map.c  from #curr_next cn inner join #brk map 
    on 
    (map.c between cn.curr_c and cn.next_c or map.c between cn.next_c and curr_c)
    and 
    (map.r between cn.curr_r and cn.next_r or map.r between cn.next_r and curr_r)
) t

-- Q2 

-- generate a matrix with app the locations on the map
-- Put obstacles to all the possible location on the map 
-- to check if can reach a dummy obstacle location (-1, -1), which is placed outside the map
-- simulate the walk, if meet obstacle, turn right 
    -- initial is fcing up, (r_move,c_move) = (-1, 0)
    -- turn right --> facing right --> (0, 1) 
    -- turn right --> facing down --> (1, 0)
    -- turn right --> facing left --> (0, -1)
    -- it's always (prev_c_move, prev_r_move * -1 )
-- if it cannot reac outside after 1000000 iterations, meaning it stucked in the loop


drop table if exists #obstacle_loc

select distinct r, c 
into #obstacle_loc 
from #brk
where ch != '^'
union all 
select -1, -1 

-- select * from #obstacle_loc

drop table if exists #initial

select 
start_r = r
, start_c = c
, max_r = (select r = max(r) from #brk )
, max_c = (select c= max(c) from #brk )
into #initial
from #brk where ch = '^'


update #brk set ch = '.' where ch = '^'


-- select * from #initial

; with moving AS
(
    select
    n = 1
    , obstacle_r = o.r 
    , obstacle_c = o.c
    , curr_r = start_r
    , curr_c = start_c
    , move_r = -1
    , move_c = 0
    from #obstacle_loc o,  #initial
    
    union all 
    
    select 
    n + 1 
    , obstacle_r 
    , obstacle_c
    , nxt_r = case when nxt.newblock = '.' then next_r
                   else curr_r
              end 
    , nxt_c = case when nxt.newblock = '.' then next_c
                   else curr_c
              end
    , move_r = case when nxt.newblock = '.' then move_r when nxt.newblock = '#' then move_c end
    , move_c =  case when nxt.newblock = '.' then move_c when nxt.newblock = '#' then -move_r end
    from moving inner join #initial ini on 1=1 -- dummy cross join
    cross apply (select next_r = curr_r + move_r
                        , next_c = curr_c + move_c
                ) nxt_r_c
    cross apply 
                (select 
                    newblock = case when not (next_r between 1 and ini.max_r)
                                          or
                                         not (next_c  between 1 and ini.max_c)
                                then NULL
                                when next_r = obstacle_r 
                                    and 
                                    next_c = obstacle_c 
                                then '#'
                                else 
                                (select ch from #brk where r = next_r and c = next_c)
                                end
                ) nxt
   where n < ini.max_r * ini.max_c and nxt.newblock is not null 
)
select res = count(*) 
from 
(select distinct curr_r, curr_c from moving, #initial
    where n = max_r * max_c
) _
option (maxrecursion 0)


