-- idea 
-- break the number string
-- go through current pos and next pos, apply both + and * 
-- store the possible number in res
-- find all the possible numbers with + and * apply in each group

drop table if exists #landing

select 
grp = ROW_NUMBER() over (order by cast(ans as bigint))
, ans = cast(ans as bigint)
, col = col + ' ' into #landing 
from #input

drop table if exists #base 

; with cte as (
select 
grp
, ans 
, pos = 1
, num = cast(left(col, charindex(' ', col) - 1) as bigint)
, rem = right(col, datalength(col) - charindex(' ', col))
from #landing 
union all 

select 
grp
, ans
, pos + 1 
, num = cast(left(rem, charindex(' ', rem) - 1) as bigint)
, rem = right(rem, datalength(rem) - charindex(' ', rem))
from cte 
where datalength(rem) > 1 
)
select 
* into #base
from cte 
order by 1 , pos
option (maxrecursion 0 )

create index idx on #base(grp, pos)

drop table if exists #calc_res

; with cte as 
(
select 
grp
, calc_step = 1
, ans
, pos
, res = num
 from #base 
union all 
 select  
 curr.grp
, calc_step + 1
, curr.ans 
, nxt.pos
, res = calc.res
from cte curr inner join #base nxt 
on curr.grp = nxt.grp
and nxt.pos = curr.pos + 1 
cross apply (select res = curr.res + nxt.num 
            union all 
            select res = curr.res * nxt.num
            ) calc
)
select * 
into #calc_res
 from cte 
option (maxrecursion 0 )

