drop table if exists #base 

; with cte as 
(
select 
r = cast(id as int)
, c = 1 
, ch = left(col, 1)
, rem = right(col, len(col) - 1)
from #input

union all 

select 
r
, c + 1 
, ch = left(rem, 1)
, rem = right(rem, len(rem) - 1)
from cte
where len(rem) > 0
)
select 
pos = cast(mc*(r-1) + c as varchar(100))
, y = r
,x =  c
, val = ch  
into #base 
from cte, (select mc = max(c) from cte) _
OPTION(maxrecursion 0)


drop table if exists #islands

-- Step 1: Identify islands within rows
;with i as (
    select *,
        iif(lag(Val) over(partition by Y order by X) = Val, 1, 0) as IsContX,
        iif(lag(Val) over(partition by X order by Y) = Val, 0, 1) as IsFirstY,
        iif(lead(Val) over(partition by X order by Y) = Val, 0, 1) as IsLastY
    from #base
)
, i1 as (
    select X, Y, Val, 
        max(FirstX) over(partition by Y order by X) as FirstX, 
        IsFirstY, IsLastY
    from i
    cross apply (select iif(IsContX = 0, X, null) as FirstX) f
)
select Val, Y, FirstX, max(X) as LastX, min(IsFirstY) as IsFirstY, min(IsLastY) as IsLastY,
    row_number() over(order by Y, FirstX) as ID,
    string_agg(cast(X as varchar(max)), ',') as Xs
into #Islands
from i1
group by Val, Y, FirstX;

-- Index to optimize joins
create unique clustered index IX_#Islands on #Islands(Y, Val, FirstX);


drop table if exists #rec 

-- Step 2: Group contiguous rows into islands
;with rec as (
    select Val, Y, FirstX, LastX, ID as root_segment, ID as IslandID, 0 as StepID
    from #Islands
    where IsFirstY = 1

    union all

    select r.Val, i.Y, i.FirstX, i.LastX, r.root_segment, i.ID as IslandID, StepID + 1
    from rec r
    inner join #Islands i 
        on i.Val = r.Val
        and i.Y = r.Y + 1
        and (
            i.FirstX between r.FirstX and r.LastX
            or r.FirstX between i.FirstX and i.LastX
        )
)
select distinct root_segment, IslandID
into #Rec
from rec;

-- Step 3: Map islands to master groups
select r1.root_segment, min(r2.root_segment) as Newroot_segment
into #MasterGroups
from #Rec r1
cross apply (
    select top 1 *
    from #Rec r2
    where r2.root_segment <= r1.root_segment
        and r2.IslandID = r1.IslandID
) r2
group by r1.root_segment;

-- Step 4: Consolidate islands into master groups
select distinct m.Newroot_segment as root_segment, r.IslandID
into #Rec1
from #MasterGroups m
inner join #Rec r on r.root_segment = m.root_segment;

-- Step 5: Calculate area and perimeter for each group
;with i as (
    select root_segment, i.Val, Y, sum(Area) as Area,
        string_agg(Xs, ',') as Xs,
        count(*) * 2 + min(IsFirstY) * sum(Area) as Perimeter
    from #Rec1 r
    inner join #Islands i on i.ID = r.IslandID
    cross apply (select LastX - FirstX + 1 as Area) a
    group by root_segment, i.Val, Y
)
, i1 as (
    select root_segment, Val, Y, Area, Perimeter, Xs, lead(Xs) over (partition by root_segment order by Y) as NextXs
    from i
)
, i2 as (
    select root_segment, Val, sum(Area) as Area, sum(i1.Perimeter + e.Perimeter) as Perimeter
    from i1
    cross apply (
        select sum(iif(c.[value] + p.[value] is null, 1, 0)) as Perimeter
        from string_split(Xs, ',') c
        full outer join string_split(NextXs, ',') p on p.[value] = c.[value]
    ) e
    group by root_segment, Val
)
select sum(Area * Perimeter) as TotalCost
from i2
order by 1;






-- Alternative calculation for debugging
;with i as (
    select root_segment, i.Val, Y, string_agg(Xs, ',') as Xs
    from #Rec1 r
    inner join #Islands i on i.ID = r.IslandID
    --cross apply (select LastX - FirstX + 1 as Area) a
    group by root_segment, i.Val, Y
)
, i1 as (
    select root_segment, Val, cast([value] as int) as X, Y
    from i
    cross apply string_split(Xs, ',')
)  -- select * from i1
, i2 as (
    select *,
        lag(Y,1) over(partition by root_segment, X order by Y) as PrevY,
        lead(Y,1) over(partition by root_segment, X order by Y) as NextY,
        lag(X,1) over(partition by root_segment, Y order by X) as PrevX,
        lead(X,1) over(partition by root_segment, Y order by X) as NextX
    from i1
)
, i3 as (
    select *,
        iif(isnull(PrevX, -1) < X - 1, 1, 0) as IsWest,
        iif(isnull(NextX, 9999) > X + 1, 1, 0) as IsEast,
        iif(isnull(PrevY, -1) < Y - 1, 1, 0) as IsNorth,
        iif(isnull(NextY, 9999) > Y + 1, 1, 0) as IsSouth
    from i2
) -- select * from i3 
, i4 as (
    select *,
        iif(IsWest = 1 and isnull(PrevY, -1) = Y - 1 and lag(IsWest, 1, 0) over(partition by root_segment, X order by Y) = 1, 0, IsWest) as West,
        iif(IsEast = 1 and isnull(PrevY, -1) = Y - 1 and lag(IsEast, 1, 0) over(partition by root_segment, X order by Y) = 1, 0, IsEast) as East,
        iif(IsNorth = 1 and isnull(PrevX, -1) = X - 1 and lag(IsNorth, 1, 0) over(partition by root_segment, Y order by X) = 1, 0, IsNorth) as North,
        iif(IsSouth = 1 and isnull(PrevX, -1) = X - 1 and lag(IsSouth, 1, 0) over(partition by root_segment, Y order by X) = 1, 0, IsSouth) as South
    from i3
), i5 as (
    select root_segment, Val,
        sum(West + East + North + South) as Perimeter,
        count(*) as Area
    from i4
    group by root_segment, Val
)
select sum(Perimeter * Area) as DebugTotal
from i5;



